// dllexport on the definition and dllimport on the declaration.
// The XXX_EXPLICIT_INSTANTIATION_DECLARATION_DLLAPI macro
// should be set on the declaration while
// the XXX_EXPLICIT_INSTANTIATION_DEFINITION_DLLAPI macro
// should be set on the definition.
# if defined _WIN32 || defined __CYGWIN__
// On Microsoft Windows, use dllimport and dllexport to tag symbols.
#  define CITO_DLLIMPORT __declspec(dllimport)
#  define CITO_DLLEXPORT __declspec(dllexport)
#  define CITO_DLLLOCAL
#  define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLIMPORT __declspec(dllimport)
#  define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLEXPORT
#  define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLIMPORT __declspec(dllimport)
#  define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLEXPORT __declspec(dllexport)
# else
// On Linux, for GCC >= 4, tag symbols using GCC extension.
#  if __GNUC__ >= 4
#   define CITO_DLLIMPORT __attribute__ ((visibility("default")))
#   define CITO_DLLEXPORT __attribute__ ((visibility("default")))
#   define CITO_DLLLOCAL  __attribute__ ((visibility("hidden")))
#   define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLIMPORT __attribute__ ((visibility("default")))
#   define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLEXPORT __attribute__ ((visibility("default")))
#   define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLIMPORT
#   define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLEXPORT
#  else
// Otherwise (GCC < 4 or another compiler is used), export everything.
#   define CITO_DLLIMPORT
#   define CITO_DLLEXPORT
#   define CITO_DLLLOCAL
#   define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLIMPORT
#   define CITO_EXPLICIT_INSTANTIATION_DECLARATION_DLLEXPORT
#   define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLIMPORT
#   define CITO_EXPLICIT_INSTANTIATION_DEFINITION_DLLEXPORT
#  endif // __GNUC__ >= 4
# endif // defined _WIN32 || defined __CYGWIN__

